import React, { useState } from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useNotification } from '../contexts/NotificationContext';
import NotificationContainer from '../components/NotificationContainer';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [selectedAccount, setSelectedAccount] = useState('');
  
  const { login, loading, isAuthenticated, error, clearError } = useAuth();
  const { showError, showSuccess } = useNotification();

  const testAccounts = [
    { email: 'admin@acme.test', label: 'Acme Admin', role: 'Admin', tenant: 'Acme' },
    { email: 'user@acme.test', label: 'Acme Member', role: 'Member', tenant: 'Acme' },
    { email: 'admin@globex.test', label: 'Globex Admin', role: 'Admin', tenant: 'Globex' },
    { email: 'user@globex.test', label: 'Globex Member', role: 'Member', tenant: 'Globex' },
    { email: 'john.doe@example.com', label: 'Member 1', role: 'Member', tenant: 'Acme (Invitable)' },
    { email: 'joe.smith@example.com', label: 'Member 2', role: 'Member', tenant: 'Globex (Invitable)' }
  ];

  // Redirect if already logged in
  if (isAuthenticated) {
    return <Navigate to="/dashboard" replace />;
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    clearError();

    if (!email || !password) {
      showError('Please enter both email and password');
      return;
    }

    const result = await login(email, password);
    if (result.success) {
      showSuccess('Login successful!');
    } else {
      showError(result.message || 'Login failed');
    }
  };

  const handleTestAccountSelect = (testEmail) => {
    setEmail(testEmail);
    setPassword('password');
    setSelectedAccount(testEmail);
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col justify-center py-12 sm:px-6 lg:px-8">
      <NotificationContainer />
      
      <div className="sm:mx-auto sm:w-full sm:max-w-md">
        <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
          Sign in to Notes SaaS
        </h2>
        <p className="mt-2 text-center text-sm text-gray-600">
          Multi-tenant Notes Application
        </p>
      </div>

      <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-md">
        <div className="bg-white py-8 px-4 shadow sm:rounded-lg sm:px-10">
          <form className="space-y-6" onSubmit={handleSubmit}>
            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                Email address
              </label>
              <div className="mt-1">
                <input
                  id="email"
                  name="email"
                  type="email"
                  autoComplete="email"
                  required
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="input-field"
                  placeholder="Enter your email"
                />
              </div>
            </div>

            <div>
              <label htmlFor="password" className="block text-sm font-medium text-gray-700">
                Password
              </label>
              <div className="mt-1">
                <input
                  id="password"
                  name="password"
                  type="password"
                  autoComplete="current-password"
                  required
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="input-field"
                  placeholder="Enter your password"
                />
              </div>
            </div>

            {error && (
              <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded-md text-sm">
                {error}
              </div>
            )}

            <div>
              <button
                type="submit"
                disabled={loading}
                className="w-full btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? 'Signing in...' : 'Sign in'}
              </button>
            </div>
          </form>

          <div className="mt-8">
            <div className="relative">
              <div className="absolute inset-0 flex items-center">
                <div className="w-full border-t border-gray-300" />
              </div>
              <div className="relative flex justify-center text-sm">
                <span className="px-2 bg-white text-gray-500">Test Accounts</span>
              </div>
            </div>

            <div className="mt-6">
              <div className="grid grid-cols-1 gap-3">
                {testAccounts.map((account) => (
                  <button
                    key={account.email}
                    type="button"
                    onClick={() => handleTestAccountSelect(account.email)}
                    className={`w-full flex justify-between items-center px-4 py-3 border rounded-md text-sm font-medium transition-colors ${
                      selectedAccount === account.email
                        ? 'border-blue-500 bg-blue-50 text-blue-700'
                        : 'border-gray-300 bg-white text-gray-700 hover:bg-gray-50'
                    }`}
                  >
                    <span>{account.label}</span>
                    <span className="text-xs text-gray-500">
                      {account.role} â€¢ {account.tenant}
                    </span>
                  </button>
                ))}
              </div>
              <p className="mt-3 text-xs text-gray-500 text-center">
                All test accounts use password: <code className="bg-gray-100 px-1 rounded">password</code>
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
