import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useNotification } from '../contexts/NotificationContext';
import NotificationContainer from '../components/NotificationContainer';
import NotesManager from '../components/NotesManager';
import TenantUpgrade from '../components/TenantUpgrade';
import UserManagement from '../components/UserManagement';
import MyInvitations from '../components/MyInvitations';
import api from '../services/api';
import { 
  DocumentTextIcon, 
  UserIcon, 
  ArrowRightOnRectangleIcon,
  ChartBarIcon,
  UsersIcon,
  EnvelopeIcon
} from '@heroicons/react/24/outline';

const Dashboard = () => {
  const { user, logout } = useAuth();
  const { showError } = useNotification();
  const location = useLocation();
  
  const [tenantStats, setTenantStats] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchTenantStats();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const fetchTenantStats = async () => {
    try {
      const response = await api.getTenantStats();
      if (response.success) {
        setTenantStats(response.data);
      }
    } catch (error) {
      showError('Failed to fetch tenant statistics');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    logout();
  };

  const navigation = [
    { name: 'Notes', href: '/dashboard', icon: DocumentTextIcon, current: location.pathname === '/dashboard' },
    { name: 'Statistics', href: '/dashboard/stats', icon: ChartBarIcon, current: location.pathname === '/dashboard/stats' },
    { name: 'Invitations', href: '/dashboard/invitations', icon: EnvelopeIcon, current: location.pathname === '/dashboard/invitations' },
  ];

  // Add Users tab for admin users
  if (user?.role === 'admin') {
    navigation.push({
      name: 'Users', 
      href: '/dashboard/users', 
      icon: UsersIcon, 
      current: location.pathname === '/dashboard/users'
    });
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <NotificationContainer />
      
      {/* Header */}
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div className="flex items-center">
              <h1 className="text-3xl font-bold text-gray-900">Notes SaaS</h1>
              {tenantStats && (
                <div className="ml-4 flex items-center space-x-4">
                  <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                    {tenantStats.tenant.name}
                  </span>
                  <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                    tenantStats.tenant.plan === 'pro' 
                      ? 'bg-green-100 text-green-800' 
                      : 'bg-yellow-100 text-yellow-800'
                  }`}>
                    {tenantStats.tenant.plan.toUpperCase()} Plan
                  </span>
                </div>
              )}
            </div>
            
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <UserIcon className="h-5 w-5 text-gray-400" />
                <span className="text-sm text-gray-700">{user?.email}</span>
                <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-gray-100 text-gray-800">
                  {user?.role}
                </span>
              </div>
              <button
                onClick={handleLogout}
                className="inline-flex items-center px-3 py-2 border border-gray-300 shadow-sm text-sm leading-4 font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                <ArrowRightOnRectangleIcon className="h-4 w-4 mr-1" />
                Logout
              </button>
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Navigation */}
        <nav className="flex space-x-8 mb-8">
          {navigation.map((item) => {
            const Icon = item.icon;
            return (
              <Link
                key={item.name}
                to={item.href}
                className={`${
                  item.current
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                } whitespace-nowrap py-2 px-1 border-b-2 font-medium text-sm flex items-center`}
              >
                <Icon className="h-5 w-5 mr-2" />
                {item.name}
              </Link>
            );
          })}
        </nav>

        {/* Content */}
        <Routes>
          <Route 
            path="/" 
            element={
              <NotesManager 
                tenantStats={tenantStats} 
                onStatsUpdate={fetchTenantStats} 
              />
            } 
          />
          <Route 
            path="/stats" 
            element={
              <div className="space-y-6">
                <div className="card">
                  <h3 className="text-lg font-medium text-gray-900 mb-4">Tenant Statistics</h3>
                  {tenantStats && (
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                      <div className="bg-blue-50 p-4 rounded-lg">
                        <dt className="text-sm font-medium text-blue-600">Tenant</dt>
                        <dd className="text-2xl font-bold text-blue-900">{tenantStats.tenant.name}</dd>
                      </div>
                      <div className="bg-green-50 p-4 rounded-lg">
                        <dt className="text-sm font-medium text-green-600">Plan</dt>
                        <dd className="text-2xl font-bold text-green-900">{tenantStats.tenant.plan.toUpperCase()}</dd>
                      </div>
                      <div className="bg-purple-50 p-4 rounded-lg">
                        <dt className="text-sm font-medium text-purple-600">Notes Created</dt>
                        <dd className="text-2xl font-bold text-purple-900">{tenantStats.noteCount}</dd>
                      </div>
                      <div className="bg-yellow-50 p-4 rounded-lg">
                        <dt className="text-sm font-medium text-yellow-600">Note Limit</dt>
                        <dd className="text-2xl font-bold text-yellow-900">
                          {tenantStats.noteLimit ? tenantStats.noteLimit : 'Unlimited'}
                        </dd>
                      </div>
                    </div>
                  )}
                </div>
                
                {user?.role === 'admin' && tenantStats?.tenant.plan === 'free' && (
                  <TenantUpgrade 
                    tenant={tenantStats.tenant} 
                    onUpgrade={fetchTenantStats} 
                  />
                )}
              </div>
            } 
          />
          <Route 
            path="/invitations" 
            element={<MyInvitations />} 
          />
          {user?.role === 'admin' && (
            <Route 
              path="/users" 
              element={<UserManagement />} 
            />
          )}
        </Routes>
      </div>
    </div>
  );
};

export default Dashboard;
