import React, { useState, useRef, useEffect } from 'react';
import { 
  XMarkIcon, 
  BoldIcon, 
  ItalicIcon, 
  UnderlineIcon,
  ListBulletIcon,
  FaceSmileIcon
} from '@heroicons/react/24/outline';

const NoteForm = ({ note, onSubmit, onCancel }) => {
  const [formData, setFormData] = useState({
    title: note?.title || '',
    content: note?.content || ''
  });
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const contentRef = useRef(null);

  // Common emojis for quick access
  const commonEmojis = [
    '😀', '😃', '😄', '😁', '😆', '😅', '😂', '🤣', '😊', '😇',
    '🙂', '🙃', '😉', '😌', '😍', '🥰', '😘', '😗', '😙', '😚',
    '😋', '😛', '😝', '😜', '🤪', '🤨', '🧐', '🤓', '😎', '🤩',
    '🥳', '😏', '😒', '😞', '😔', '😟', '😕', '🙁', '☹️', '😣',
    '😖', '😫', '😩', '🥺', '😢', '😭', '😤', '😠', '😡', '🤬',
    '👍', '👎', '👌', '✌️', '🤞', '🤟', '🤘', '🤙', '👈', '👉',
    '👆', '👇', '☝️', '✋', '🤚', '🖐️', '🖖', '👋', '🤝', '🙏',
    '❤️', '🧡', '💛', '💚', '💙', '💜', '🖤', '🤍', '🤎', '💔',
    '❣️', '💕', '💞', '💓', '💗', '💖', '💘', '💝', '💟', '☮️',
    '🔥', '⭐', '🌟', '✨', '⚡', '💯', '💢', '💥', '💫', '💦'
  ];

  useEffect(() => {
    // Focus on content when component mounts
    if (contentRef.current && !note) {
      contentRef.current.focus();
    }
  }, [note]);

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.title.trim()) {
      newErrors.title = 'Title is required';
    } else if (formData.title.length > 200) {
      newErrors.title = 'Title must be less than 200 characters';
    }
    
    if (!formData.content.trim()) {
      newErrors.content = 'Content is required';
    } else if (formData.content.length > 10000) {
      newErrors.content = 'Content must be less than 10,000 characters';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    setLoading(true);
    try {
      await onSubmit({
        title: formData.title.trim(),
        content: formData.content.trim()
      });
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    
    // Clear error when user starts typing
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: null }));
    }
  };

  const insertTextAtCursor = (text) => {
    const textarea = contentRef.current;
    if (!textarea) return;

    const start = textarea.selectionStart;
    const end = textarea.selectionEnd;
    const currentContent = formData.content;
    
    const newContent = currentContent.substring(0, start) + text + currentContent.substring(end);
    handleInputChange('content', newContent);
    
    // Restore cursor position
    setTimeout(() => {
      textarea.focus();
      textarea.setSelectionRange(start + text.length, start + text.length);
    }, 0);
  };

  const wrapSelectedText = (prefix, suffix = '') => {
    const textarea = contentRef.current;
    if (!textarea) return;

    const start = textarea.selectionStart;
    const end = textarea.selectionEnd;
    const selectedText = formData.content.substring(start, end);
    const currentContent = formData.content;
    
    if (selectedText) {
      // Wrap selected text
      const wrappedText = prefix + selectedText + (suffix || prefix);
      const newContent = currentContent.substring(0, start) + wrappedText + currentContent.substring(end);
      handleInputChange('content', newContent);
      
      // Restore selection
      setTimeout(() => {
        textarea.focus();
        textarea.setSelectionRange(start + prefix.length, start + prefix.length + selectedText.length);
      }, 0);
    } else {
      // Insert formatting markers
      const placeholder = suffix ? 'text' : 'formatted text';
      const wrappedText = prefix + placeholder + (suffix || prefix);
      insertTextAtCursor(wrappedText);
      
      // Select the placeholder
      setTimeout(() => {
        textarea.focus();
        textarea.setSelectionRange(start + prefix.length, start + prefix.length + placeholder.length);
      }, 0);
    }
  };

  const handleFormatting = (type) => {
    switch (type) {
      case 'bold':
        wrapSelectedText('**');
        break;
      case 'italic':
        wrapSelectedText('*');
        break;
      case 'underline':
        wrapSelectedText('<u>', '</u>');
        break;
      case 'bulletList':
        insertBulletPoint();
        break;
      default:
        break;
    }
  };

  const insertBulletPoint = () => {
    const textarea = contentRef.current;
    if (!textarea) return;

    const start = textarea.selectionStart;
    const currentContent = formData.content;
    
    // Check if we're at the beginning of a line
    const beforeCursor = currentContent.substring(0, start);
    const isNewLine = beforeCursor === '' || beforeCursor.endsWith('\n');
    
    const bulletText = isNewLine ? '• ' : '\n• ';
    insertTextAtCursor(bulletText);
  };

  const insertEmoji = (emoji) => {
    insertTextAtCursor(emoji);
    setShowEmojiPicker(false);
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-10 mx-auto p-5 border w-full max-w-4xl shadow-lg rounded-md bg-white m-4">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-medium text-gray-900">
            {note ? 'Edit Note' : 'Create New Note'}
          </h3>
          <button
            onClick={onCancel}
            className="p-1 text-gray-400 hover:text-gray-600 transition-colors"
          >
            <XMarkIcon className="h-6 w-6" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label htmlFor="title" className="block text-sm font-medium text-gray-700 mb-1">
              Title
            </label>
            <input
              type="text"
              id="title"
              value={formData.title}
              onChange={(e) => handleInputChange('title', e.target.value)}
              className={`input-field ${errors.title ? 'border-red-500' : ''}`}
              placeholder="Enter note title..."
              maxLength={200}
            />
            {errors.title && (
              <p className="mt-1 text-sm text-red-600">{errors.title}</p>
            )}
            <p className="mt-1 text-xs text-gray-500">
              {formData.title.length}/200 characters
            </p>
          </div>

          <div>
            <label htmlFor="content" className="block text-sm font-medium text-gray-700 mb-1">
              Content
            </label>
            
            {/* Formatting Toolbar */}
            <div className="flex items-center space-x-2 p-2 bg-gray-50 border border-gray-300 rounded-t-md border-b-0">
              <button
                type="button"
                onClick={() => handleFormatting('bold')}
                className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-200 rounded transition-colors"
                title="Bold (wrap with **text**)"
              >
                <BoldIcon className="h-4 w-4" />
              </button>
              
              <button
                type="button"
                onClick={() => handleFormatting('italic')}
                className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-200 rounded transition-colors"
                title="Italic (wrap with *text*)"
              >
                <ItalicIcon className="h-4 w-4" />
              </button>
              
              <button
                type="button"
                onClick={() => handleFormatting('underline')}
                className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-200 rounded transition-colors"
                title="Underline (wrap with <u>text</u>)"
              >
                <UnderlineIcon className="h-4 w-4" />
              </button>
              
              <div className="w-px h-6 bg-gray-300"></div>
              
              <button
                type="button"
                onClick={() => handleFormatting('bulletList')}
                className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-200 rounded transition-colors"
                title="Bullet Point"
              >
                <ListBulletIcon className="h-4 w-4" />
              </button>
              
              <div className="w-px h-6 bg-gray-300"></div>
              
              <div className="relative">
                <button
                  type="button"
                  onClick={() => setShowEmojiPicker(!showEmojiPicker)}
                  className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-200 rounded transition-colors"
                  title="Insert Emoji"
                >
                  <FaceSmileIcon className="h-4 w-4" />
                </button>
                
                {showEmojiPicker && (
                  <div className="absolute top-full left-0 mt-1 bg-white border border-gray-300 rounded-md shadow-lg p-3 z-10 max-w-xs">
                    <div className="grid grid-cols-10 gap-1 max-h-32 overflow-y-auto">
                      {commonEmojis.map((emoji, index) => (
                        <button
                          key={index}
                          type="button"
                          onClick={() => insertEmoji(emoji)}
                          className="p-1 hover:bg-gray-100 rounded text-lg transition-colors"
                          title={`Insert ${emoji}`}
                        >
                          {emoji}
                        </button>
                      ))}
                    </div>
                  </div>
                )}
              </div>
              
              <div className="flex-1"></div>
              
              <p className="text-xs text-gray-500">
                Use **bold**, *italic*, <u>underline</u> for formatting
              </p>
            </div>
            
            <textarea
              ref={contentRef}
              id="content"
              rows={12}
              value={formData.content}
              onChange={(e) => handleInputChange('content', e.target.value)}
              className={`input-field resize-none rounded-t-none border-t-0 ${errors.content ? 'border-red-500' : ''}`}
              placeholder="Write your note content here... 

You can use:
• **bold text** for emphasis
• *italic text* for style  
• <u>underlined text</u> for importance
• Bullet points for lists
• Emojis for fun! 😊"
              maxLength={10000}
              style={{ fontFamily: 'system-ui, -apple-system, sans-serif' }}
            />
            {errors.content && (
              <p className="mt-1 text-sm text-red-600">{errors.content}</p>
            )}
            <p className="mt-1 text-xs text-gray-500">
              {formData.content.length}/10,000 characters
            </p>
          </div>

          <div className="flex justify-end space-x-3 pt-4">
            <button
              type="button"
              onClick={onCancel}
              className="btn-secondary"
              disabled={loading}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn-primary"
              disabled={loading || !formData.title.trim() || !formData.content.trim()}
            >
              {loading ? 'Saving...' : (note ? 'Update Note' : 'Create Note')}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default NoteForm;
